-- Correções para a coluna: linha_de_capacitacao
UPDATE capacitacoes
SET linha_de_capacitacao = CASE linha_de_capacitacao
    WHEN 'LOGISTICA' THEN 'LOGÍSTICA'
    WHEN 'EQUIPE' THEN 'EQUIPES'
    WHEN 'PI' THEN 'PI E INOVAÇÃO'
    WHEN 'INFORMÁTICA - SISTEMA INFORMATIZADOS DO GOVERNO FEDERAL' THEN 'TECNOLOGIA E SISTEMAS DE GOVERNO'
    WHEN 'SISTEMAS DO GOVERNO FEDERAL' THEN 'TECNOLOGIA E SISTEMAS DE GOVERNO'
